import ReactDOM from 'react-dom'
import React, { useState, useEffect, useMemo } from 'react'
// A THREE.js React renderer, see: https://github.com/drcmda/react-three-fiber
import { useThree, Canvas } from 'react-three-fiber'
// A React animation lib, see: https://github.com/react-spring/react-spring
import { useSpring, useTransition, animated } from 'react-spring'
import { OrbitControls } from 'drei'
import ReactTypingEffect from 'react-typing-effect';
import './styles.css'

import Space from './space'

const Scene = () => {
    const [active, setActive] = useState(false)
    const [stage, setStage] = useState(0)

    const headlineTransitions = useTransition(!active, null, {
        from: { position: 'absolute', top: '70%', opacity: 0 },
        enter: { opacity: 1, top: '66%' },
        leave: { opacity: 0, top: '70%' }
    })

    const labelProps = useSpring({ opacity: active ? 1 : 0 })

    return (
        <>
            <Canvas>
                <OrbitControls enableZoom={false}/>
                <Space active={active} setActive={setActive} stage={stage} setStage={setStage} />
            </Canvas>
            {headlineTransitions.map(
                ({ item, key, props }) =>
                item && (
                    <animated.div id="headline" key={key} style={props}>
                    <ReactTypingEffect 
                        text={["theEmpire.dev", "Inter-galactic software."]} 
                        cursorRenderer={cursor => <h1>{cursor}</h1>}
                        displayTextRenderer={(text, i) => <h1>{text}</h1>}  
                        speed={80}
                        eraseSpeed={100}
                    />
                    </animated.div>
                )
            )}
            <animated.div id="label" style={labelProps}>
                <h2>Planet LV2D3V</h2>
                <h4>(scroll down to discover more)</h4>
            </animated.div>
        </>
    )
}

ReactDOM.render(<Scene />, document.getElementById('root'))
